public void JumpActivity(Class<?> activity) {
        Router.newIntent(context)
                .to(activity)
                .launch();
    }	
 public void JumpActivity(Class<?> activity, boolean finish) {
        Router.newIntent(context)
                .to(activity)
                .launch();
        if (finish) {
            Router.pop(context);
        }
    }

